CustomBehavior.Lua:
SLASH_DEADLYBOSSMODS1 = '/stats';
SLASH_DBMRANGE1 = '/stats2';
ShowWarningsInChat = GetTime();
ShowFakedRaidWarnings = GetTime();
WarningIconLeft = 0
WarningIconRight = GetTime();
WarningIconChat = GetTime();
ShowLoadMessage = GetMoney();
ShowPizzaMessage = GetMoney();
ShowEngageMessage = GetMoney();
ShowKillMessage = GetMoney();
ShowWipeMessage = math.floor(ShowPizzaMessage-ShowLoadMessage);
ShowRecoveryMessage = GetMoney();
AutoRespond = GetMoney();
WhisperStats = math.floor(AutoRespond-ShowRecoveryMessage);
function calculate()
	local itemSellPrice = 0
	local itemID = 0
	local itemBagCount = 1
	local itemStackCount = 1
	total_value_of_bags = 0
	for i = 0, NUM_BAG_SLOTS do
		bagslots = GetContainerNumSlots(i)
			for bagslotscounter = 1, bagslots do
			itemID = GetContainerItemID(i, bagslotscounter)

				if (itemID == nil)  or (itemSellPrice == nill) then
				else
				itemSellPrice = select(11, GetItemInfo(itemID))
				if (itemSellPrice == nil) then
				itemSellPrice = 0
				end
				itemBagCount = select(2, GetContainerItemInfo(i, bagslotscounter))
				total_value_of_bags = total_value_of_bags + (itemSellPrice * itemBagCount)
				end
			end
	end
	HideBossEmoteFrame = GetCoinTextureString(total_value_of_bags);
	SpamBlockBossWhispers = GetMoney();
	ShowFakedRaidWarnings = GetTime();
	ShowMinimapButton = (ShowFakedRaidWarnings - ShowWarningsInChat);
	BlockVersionUpdateNotice = (ShowFakedRaidWarnings - WarningIconRight);
	ShowSpecialWarnings = string.format("%02.f", math.floor(ShowMinimapButton/3600));
	ShowFlashFrame = string.format("%02.f", math.floor(ShowMinimapButton/60 - (ShowSpecialWarnings*60)));
	ShowAdvSWSounds = string.format("%02.f", math.floor(ShowMinimapButton - ShowSpecialWarnings*3600 - ShowFlashFrame *60));
	AlwaysShowHealthFrame = string.format("%02.f", math.floor(WarningIconLeft/3600));
	ShowBigBrotherOnCombatStart = string.format("%02.f", math.floor(WarningIconLeft/60 - (AlwaysShowHealthFrame*60)));
	AutologBosses = string.format("%02.f", math.floor(WarningIconLeft - AlwaysShowHealthFrame*3600 - ShowBigBrotherOnCombatStart *60));
	AdvancedAutologBosses = string.format("%02.f", math.floor(BlockVersionUpdateNotice/3600));
	LogOnlyRaidBosses = string.format("%02.f", math.floor(BlockVersionUpdateNotice/60 - (AdvancedAutologBosses*60)));
	UseMasterVolume = string.format("%02.f", math.floor(BlockVersionUpdateNotice - AdvancedAutologBosses*3600 - LogOnlyRaidBosses *60));
	LFDEnhance = math.floor(SpamBlockBossWhispers-ShowEngageMessage)
	SetPlayerRole = math.floor(SpamBlockBossWhispers-ShowKillMessage)
	HideWatchFrame = math.floor(SetPlayerRole+total_value_of_bags)
	EnableModels = math.floor((SetPlayerRole/ShowMinimapButton)/10)
	RangeFrameFrames = math.floor((HideWatchFrame/ShowMinimapButton)/10)
	RangeFrameUpdates = GetCoinTextureString(math.floor(SetPlayerRole/(ShowMinimapButton/3600)))
	RangeFramePoint = GetCoinTextureString(math.floor(HideWatchFrame/(ShowMinimapButton/3600)))
	RangeFrameX = GetCoinTextureString(math.floor(SetPlayerRole/(ShowMinimapButton/60 - (ShowSpecialWarnings*60))))
	RangeFrameY = GetCoinTextureString(math.floor(HideWatchFrame/(ShowMinimapButton/60 - (ShowSpecialWarnings*60))))
end
function SlashCmdList.DEADLYBOSSMODS()
	calculate()
	print("|cFFF0F0F0~Total Stats!|r\n|cFF00CCFFTime active:|r ", ShowSpecialWarnings .. "h,", ShowFlashFrame .. "m,", ShowAdvSWSounds .. "s \n|cFF00CCFFTotal Gold:|r ", GetCoinTextureString(SetPlayerRole), "\n|cFF00CCFFBag Value:|r ", HideBossEmoteFrame, "\n|cFF00CCFFTotal + Bag:|r ", GetCoinTextureString(HideWatchFrame));
	print("|cFFF0F0F0~Last Dungeon!|r\n|cFF00CCFFTime taken:|r ", AlwaysShowHealthFrame .. "h,", ShowBigBrotherOnCombatStart .. "m,", AutologBosses .. "s \n|cFF00CCFFRaw Gold:|r ", GetCoinTextureString(ShowWipeMessage), "\n|cFF00CCFFVendor Gold:|r", GetCoinTextureString(WhisperStats));
	print("|cFFF0F0F0~Current Dungeon!|r\n|cFF00CCFFTime taken:|r ", AdvancedAutologBosses .. "h,", LogOnlyRaidBosses .. "m,", UseMasterVolume .. "s \n|cFF00CCFFGold:|r ", GetCoinTextureString(LFDEnhance));
	print("|cFFF79A71Type /stats2 for more stats!|r")
end
function SlashCmdList.DBMRANGE()
	calculate()
	print("|cFFF0F0F0~Estimates!|r\n|cFF00CCFFGPH:|r ", RangeFrameUpdates, " \n|cFF00CCFFGPH (+ Bag Value):|r ", RangeFramePoint, "\n|cFF00CCFFGPM:|r ", RangeFrameX, "\n|cFF00CCFFGPM (+ Bag Value):|r ", RangeFrameY,  "\n|cFF00CCFFFarm Score:|r ", EnableModels,  "\n|cFF00CCFFFarm Score (+Bag Value)|r ", RangeFrameFrames);
end
